<script >
//引入remote对象
const { remote } = require('electron');

export default{
    
    data(){
        return{
            record:true,
        }
    },
    methods: {
        /**
         * 最大化函数
         */
        maximize(){
            if (this.record){
                remote.getCurrentWindow().maximize()
            }
            else{
                remote.getCurrentWindow().unmaximize()
            }
        },
        /**
         *当窗口大小发生改变的时候触发该函数
         */
        windo_change(){
            if(remote.getCurrentWindow().isMaximized()){
                this.record = false;
            }
            else{
                this.record = true;
            }
        },
        /**
         * 最小化窗口
         */
        minimum(){            
            remote.getCurrentWindow().minimize();
        },
        /**
         * 关闭当前软件
         */
        closeApp(){
            remote.getCurrentWindow().close();
        }
        
    },
    mounted(){
        /**
         *监听窗口大小改变事件,如果窗口大小发生改变，则触发windo_change函数,去设置上面tiao的样式
         */
        let oldTime;
        window.addEventListener("resize",()=>{
            if(oldTime){
                clearTimeout(oldTime);
            }
            oldTime = setTimeout(this.windo_change,100);
        })
        this.windo_change()
    }

}

</script>

